// Posts Table
const seedPosts = async (numEntries) => {
  logStep('Seeding posts...')
  const posts = []

  for (let i = 0; i < numEntries; i++) {
    const subject = faker.lorem.sentence(5)
    const body = faker.lorem.paragraphs(2)

    posts.push({
      subject: subject,
      slug: subject
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-') // spaces + symbols â†’ hyphens
        .replace(/(^-|-$)/g, ''), // trim starting/ending hyphens
      body: body,
      status: faker.helpers.arrayElement(['draft', 'published']),
      is_exclusive: faker.datatype.boolean(),
    })
  }

  const { data, error } = await supabase.from('posts').insert(posts).select('id')

  if (error) return logErrorAndExit('Posts', error)

  logStep('Posts seeded successfully.')

  return data
}

// Assigned Posts Table
const seedAssignedPosts = async (numEntries, postsIds) => {
  logStep('Seeding assigned_posts...')
  const assignedPosts = []

  for (let i = 0; i < numEntries; i++) {
    const sent = faker.datatype.boolean()
    const scheduledFor = faker.date.soon({ days: 30 })

    assignedPosts.push({
      // user_id left out on purpose for now
      post_id: faker.helpers.arrayElement(postsIds),
      scheduled_for: scheduledFor,
      sent: sent,
      sent_at: sent ? faker.date.soon({ days: 5, refDate: scheduledFor }) : null,
      is_locked: faker.datatype.boolean(),
    })
  }

  const { data, error } = await supabase.from('assigned_posts').insert(assignedPosts).select('id')

  if (error) return logErrorAndExit('assigned_posts', error)

  logStep('assigned_posts seeded successfully.')

  return data
}

const seedDatabase = async (numEntriesPerTable) => {
  const postsIds = (await seedPosts(numEntriesPerTable)).map((post) => post.id)
  await seedAssignedPosts(numEntriesPerTable, postsIds)
}

const numEntriesPerTable = 10

seedDatabase(numEntriesPerTable)

