const trackAuthChanges = () => {

if (isTrackingAuthChanges.value) return

isTrackingAuthChanges.value = true
  supabase.auth.onAuthStateChange((event,session) => {
    setTimeout(async() => {
      await setAuth(session)
    }, 0);
  })
}

  return {
